import React, { useState } from 'react';
import './Projects.css';

const Projects = () => {
  const [filter, setFilter] = useState('all');
  
  const projects = [
    {
      id: 1,
      title: "S G Blogs Website",
      description: "A full-stack blogging platform with secure authentication, MongoDB for data storage, and email notifications via Nodemailer.",
      imageUrl: "/images/projects/sgblogs-preview.jpg", 
      category: "fullstack",
      technologies: ["React.js", "Node.js", "MongoDB", "Nodemailer", "Tailwind CSS"],
      liveUrl: "https://sgblogs-frontend.vercel.app/",
      codeUrl: "https://github.com/sumitghugare1/sgblogs"
    },
    {
      id: 2,
      title: "Weather App (Android)",
      description: "Android application built using Java and Android Studio that integrates OpenWeather API to provide real-time weather updates.",
      imageUrl: "/images/projects/weather-app-preview.jpg", // Replace with actual project screenshot
      category: "mobile",
      technologies: ["Java", "Android Studio", "OpenWeather API"],
      liveUrl: "#",
      codeUrl: "https://github.com/sumitghugare1/Android-Projects"
    },
    {
      id: 3,
      title: "TimeBank",
      description: "A full-stack platform for skill exchange with JWT authentication, real-time chat, and Zustand for state management.",
      imageUrl: "/images/projects/timebank-preview.jpg", // Replace with actual project screenshot
      category: "fullstack",
      technologies: ["React", "Node.js", "Socket.IO", "Zustand", "Tailwind UI"],
      liveUrl: "#",
      codeUrl: "https://github.com/sumitghugare1/timebank"
    },
    {
      id: 4,
      title: "Automatic Plant Watering System",
      description: "Arduino-based IoT system that automates plant care with potential upgrades for wireless control and data logging.",
      imageUrl: "/images/projects/plant-watering-preview.jpg", // Replace with actual project screenshot
      category: "iot",
      technologies: ["Arduino", "IoT", "Sensors", "Automation"],
      liveUrl: "#",
      codeUrl: "#"
    }
  ];
  
  const filteredProjects = filter === 'all' 
    ? projects 
    : projects.filter(project => project.category === filter);
  
  return (
    <section id="projects" className="projects-section">
      <h2 className="section-title">My <span>Projects</span></h2>
      
      <div className="projects-filter">
        <button 
          className={filter === 'all' ? 'active' : ''} 
          onClick={() => setFilter('all')}
        >
          All
        </button>
        <button 
          className={filter === 'frontend' ? 'active' : ''} 
          onClick={() => setFilter('frontend')}
        >
          Frontend
        </button>
        <button 
          className={filter === 'fullstack' ? 'active' : ''} 
          onClick={() => setFilter('fullstack')}
        >
          Full-stack
        </button>
        <button 
          className={filter === 'mobile' ? 'active' : ''} 
          onClick={() => setFilter('mobile')}
        >
          Mobile
        </button>
        <button 
          className={filter === 'iot' ? 'active' : ''} 
          onClick={() => setFilter('iot')}
        >
          IoT
        </button>
      </div>
      
      <div className="projects-container">
        {filteredProjects.map(project => (
          <div className="project-card" key={project.id}>
            <div className="project-image" data-title={project.title}>
              <img src={project.imageUrl} alt={project.title} />
              <div className="project-links">
                <a href={project.liveUrl} target="_blank" rel="noopener noreferrer">Live Demo</a>
                <a href={project.codeUrl} target="_blank" rel="noopener noreferrer">View Code</a>
              </div>
            </div>
            <div className="project-info">
              <h3>{project.title}</h3>
              <p>{project.description}</p>
              <div className="project-tech">
                {project.technologies.map((tech, index) => (
                  <span key={index}>{tech}</span>
                ))}
              </div>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Projects;
